nta.declaration:
int grid[6][6];
bool gameCreated = false;
bool playersCreated = false;
int vehNum = 0;
int vehicles = 0;
int steps = 0;

template.declaration:
void initialize()
{
	int i;
	int j;
    vehNum = vehs;
	for(i = 0; i < 6; i++)
		for(j = 0; j < 6; j++)
			grid[i][j] = 0;

	gameCreated = true;
}

void moveUp()
{
	int tempY = y+1;
	grid[x][y+leng] = 1;
	grid[x][y] = 0;
	y = tempY;
	steps = steps+1;
}

bool checkUp()
{
	if(dir == 0 && y+leng < 6 && grid[x][y+leng] == 0)
		return true;
	else
		return false;
}

void moveDown()
{
	int tempY = y-1;
	grid[x][y-1] = 1;
	grid[x][tempY+leng] = 0;
	y = tempY;
	steps = steps+1;
}

bool checkDown()
{
	if(dir == 0 && y-1 >= 0 && grid[x][y-1] == 0)
		return true;
	else
		return false;
}

void moveLeft()
{
	int tempX = x-1;
	grid[x-1][y] = 1;
	grid[tempX+leng][y] = 0;
	x = tempX;
	steps = steps+1;
}

bool checkLeft()
{
	if(dir == 1 && x-1 >= 0 && grid[x-1][y] == 0)
		return true;
	else
		return false;
}

void moveRight()
{
	int tempX = x+1;
	grid[x+leng][y] = 1;
	grid[x][y] = 0;
	x = tempX;
	steps = steps+1;
}

bool checkRight()
{
	if(dir == 1 && x+leng < 6 && grid[x+leng][y] == 0)
		return true;
	else
		return false;
}

void create()
{
	int tempLeng;
	grid[x][y] = 1;
	if(dir == 0)
	{
		int tempY = y;
		for(tempLeng = leng; tempLeng > 1; tempLeng--)
		{
			tempY = tempY+1;
			grid[x][tempY] = 1;
		}
	}
	else if(dir == 1)
	{
		int tempX = x;
		for(tempLeng = leng; tempLeng > 1; tempLeng--)
		{
			tempX = tempX+1;
			grid[tempX][y] = 1;
		}
	}

	vehicles = vehicles+1;
	if(vehicles == vehNum)
		playersCreated = true;
}

void create()
{
	grid[x][3] = 2;
	grid[x+1][3] = 2;


	vehicles = vehicles+1;
	if(vehicles == vehNum)
		playersCreated = true;
}

void moveLeft()
{
	int tempX = x-1;
	grid[x-1][3] = 2;
	grid[x+1][3] = 0;
	x = tempX;
	steps = steps+1;
}

bool checkLeft()
{
	if(x-1 >= 0 && grid[x-1][3] == 0)
		return true;
	else
		return false;
}

void moveRight()
{
	int tempX = x+1;
	grid[x+2][3] = 2;
	grid[x][3] = 0;
	x = tempX;
	steps = steps+1;
}

bool checkRight()
{
	if(x+2 < 6 && grid[x+2][3] == 0)
		return true;
	else
		return false;
}
bool playerWin()
{
	if(x == 4)
		return true;
	else
		return false;
}

